    o.a *= v_color.a;
  #endif
  #if USE_TEXTURE
    vec4 texColor = texture(mainTexture, v_uv);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    o *= texColor;
    o.a *= v_opacity.x;
  #if USE_MIX_ALPHA
    o.a *= v_opacity.x * mixParam.x;
  #endif
  #if USE_MIX_COLOR
    float _r = 1.0 - v_opacity.x * mixParam.y;
    float _r1 = clamp( _r / mixParam.z, 0.0, 1.0);
    vec4 mixColor =  mix(startColor,midColor, _r1 );
    if( _r1 >= 1.0){
      float _r2 = clamp( (_r - mixParam.z) / (1.0 - mixParam.z), 0.0, 1.0);
