    	float viewSpacePos_z = (shadowPos.z - coeffB) / coeffA;
    	viewSpacePos_z += viewspaceDepthBias;
    	vec4 result = shadowPos;
    	result.z = viewSpacePos_z * coeffA + coeffB;
