in vec4 a_position_starttime;
in vec4 a_color;
in vec4 a_dir_life;
in float a_rndSeed;
#if !CC_INSTANCE_PARTICLE
  in vec4 a_size_uv;
  in vec4 a_rotation_uv;
#endif
#if CC_INSTANCE_PARTICLE
  in vec4 a_size_fid;
  in vec3 a_rotation;
  in vec3 a_uv;
#endif
#if CC_RENDER_MODE == 4
  in vec3 a_texCoord;
  in vec3 a_texCoord3;
  in vec3 a_normal;
  in vec4 a_color1;
#endif
vec3 unpackCurveData (sampler2D tex, vec2 coord) {
  vec4 a = texture(tex, coord);
  vec4 b = texture(tex, coord + u_sampleInfo.y);
  float c = fract(coord.x * u_sampleInfo.x);
  return mix(a.xyz, b.xyz, c);
}
vec3 unpackCurveData (sampler2D tex, vec2 coord, out float w) {
  vec4 a = texture(tex, coord);
  vec4 b = texture(tex, coord + u_sampleInfo.y);
  float c = fract(coord.x * u_sampleInfo.x);
  w = mix(a.w, b.w, c);
  return mix(a.xyz, b.xyz, c);
}
float pseudoRandom(float x) {
#if USE_VK_SHADER
  float o = x;
  x = mod(x - 1.0, 2.0) - 1.0;
  float freqVar = 10.16640753482;
  float y = sin(freqVar * floor(o * 0.5 - 0.5));
  float v = max(0.0, 1.0-abs(x));
  v *= 0.7071067812;
  v = y < 0.0 ? -v : v;
  return v;
#endif
#if !USE_VK_SHADER
  float seed = mod(x, 233280.);
  float q = (seed * 9301. + 49297.) / 233280.;
  return fract(q);
#endif
}
#if COLOR_OVER_TIME_MODULE_ENABLE
  uniform sampler2D color_over_time_tex0;
  layout(std140) uniform ColorConstant {
