    vec4 pos = vec4(a_position.xyz, 1);
  #endif
  #if CC_INSTANCE_PARTICLE
    vec4 pos = vec4(a_texCoord4.xyz, 1);
  #endif
  #if CC_RENDER_MODE == 1
    vec4 velocity = vec4(a_color1.xyz, 0);
  #endif
  #if !CC_USE_WORLD_SPACE
    pos = cc_matWorld * pos;
    #if CC_RENDER_MODE == 1
