glsl1.vert:
precision highp float;
uniform highp mat4 cc_matViewProj;
uniform highp mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute float a_dist;
varying float v_dist;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * cc_matWorld * pos;
  v_color = a_color;
  v_dist = a_dist;
  return pos;
}
void main() { gl_Position = vert(); }

glsl1.frag:
precision highp float;
uniform highp vec4 cc_time;
varying vec4 v_color;
varying float v_dist;
float Remap01(float a, float b, float t) {
  return (t-a) / (b-a);
}
float Remap(float a, float b, float c, float d, float t) {
  return Remap01(a, b, t) * (d-c) + c;
}
float Pulse(float x) {
  x *= 0.4;
  x = x + 0.5;
  float k = 16.;
  return pow(4.0 * x * (1.0 - x), k);
}
vec4 frag () {
  float alpha = abs(v_dist);
  float outlineWidth = 1.0;
  float centerAlpha = 0.;
  float alphaDist = abs(alpha - centerAlpha);
  float mask = smoothstep(outlineWidth, 0., alphaDist);
  vec4 col = vec4(vec3(1.,0.,1.), mask);
  float brightness = Remap(0., 1., 0.6, 1.0, abs(sin(cc_time.x * 2.5)));
  col.rgb += Pulse(alphaDist) * brightness;
  col.rgb *= col.a;
  return col;
}
void main() { gl_FragColor = frag(); }
