glsl1.vert:
precision highp float;
uniform highp mat4 cc_matViewProj;
uniform highp mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute float a_dist;
varying float v_dist;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * cc_matWorld * pos;
  v_color = a_color;
  v_dist = a_dist;
  return pos;
}
void main() { gl_Position = vert(); }

glsl1.frag:
precision highp float;
uniform highp vec4 cc_time;
varying vec4 v_color;
varying float v_dist;
vec3 Pallete(float t) {
  t += cc_time.x;
  vec3 dcOffset = vec3(0.5, 0.5, 0.5);
  vec3 freq = vec3(1., 1., 1.);
  vec3 phase = vec3(0., 0.3333, 0.6666);
  return dcOffset + cos(2. * 3.14159 * (freq * t + phase));
}
vec4 frag () {
  vec4 o = v_color;
  float aa = 0.05;
  float alpha = smoothstep(aa, -aa, abs(v_dist) - 1.0);
  float D = v_dist * 0.5 + 0.5;
  o.rgb = Pallete(D);
  o.rgb *= alpha;
  return o;
}
void main() { gl_FragColor = frag(); }
