glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
in vec4 a_color;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
uniform Time {
  float time;
};
uniform SPEED {
  float speed;
};
uniform sampler2D texture;
in mediump vec2 v_uv0;
in vec4 v_color;
void main () {
  vec2 uv = v_uv0.xy;
  uv.x += time * speed;
  uv.y -= time * speed;
  uv.x = fract(uv.x);
  uv.y = fract(uv.y);
  vec4 color = texture2D(texture, uv);
  gl_FragColor = color;
}

